package ch.bbw.zork;public class CommandWords {		private static final String[] validCommands = {		"go", "back", "quit", "help", "map", "itemsInRoom", "pickup", "drop"	};	public boolean isCommand(String aString) {        for (String validCommand : validCommands) {            if (validCommand.equals(aString))                return true;        }		return false;	}	public String showAll() {		StringBuilder outputStr= new StringBuilder();        for (String validCommand : validCommands) {            outputStr.append(validCommand).append("  ");        }		return outputStr.toString();	}}