package ch.bbw.zork;import java.util.ArrayList;import java.util.HashMap;public class Room {	private final String description;	private final HashMap<String, Room> exits;	private final ArrayList<Item> items;	private ArrayList<String> roomsList;	public Room(String description) {		this.description = description;		this.exits = new HashMap<>();		this.items = new ArrayList<>();		this.roomsList = new ArrayList<>();	}	public void put(Room north, Room east, Room south, Room west) {		if (north != null) {			roomsList.add(north.description);			exits.put("north", north);		}		if (east != null) {			roomsList.add(east.description);			exits.put("east", east);		}		if (south != null) {			roomsList.add(south.description);			exits.put("south", south);		}		if (west != null) {			roomsList.add(west.description);			exits.put("west", west);		}	}	public void addItem(Item item) {		items.add(item);	}	public ArrayList<Item> getItems() {		return items;	}	public String shortDescription() {		return description;	}	public String longDescription() {		StringBuilder tempStr = new StringBuilder("You are in " + description + ".\n");		tempStr.append("Items in this room:\n");		for (Item item : items) {			tempStr.append("- ").append(item.getName()).append(": ").append(item.getDesc()).append(" (").append(item.getWeightInGramm()).append("g)\n");		}		tempStr.append(exitString());		return tempStr.toString();	}	private String exitString() {		StringBuilder returnString = new StringBuilder("Exits:");		for (String key : exits.keySet()) {			returnString.append(" ").append(key);		}		return returnString.toString();	}	public Room nextRoom(String direction) {		return exits.get(direction);	}	public ArrayList<String> getRoomsList() {		return roomsList;	}	public void setRoomsList(ArrayList<String> roomsList) {		this.roomsList = roomsList;	}}